package Besics;

import java.util.Scanner;

public class BiggestTwoNumbar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter numbar");
		int numbar1=sc.nextInt();
		System.out.println("Enter numbar2");
		int numbar2=sc.nextInt();
		if(numbar1>numbar2)
		System.out.println(numbar1 + " is biggest numbar");
		else
		System.out.println(numbar2 + " is biggest numbar");
			
		
		
		
		
	}

}
